function removeAtIndexes<T>(data: T[], indexes: number[]): T[] {
  let i = 0;
  const temp = [...data];

  for (const index of indexes) {
    temp.splice(index - i, 1);
    i++;
  }

  return compact(temp).length ? temp : [];
}

export default <T>(data: T[], index?: number | number[]): T[] =>
  isUndefined(index)
    ? []
    : removeAtIndexes(
        data,
        (Array.isArray(index) ? index : [index]).sort((a, b) => a - b),
      );
import isUndefined from './isUndefined';

export default <T>(
  data: (T | undefined)[],
  from: number,
  to: number,
): (T | undefined)[] => {
  if (Array.isArray(data)) {
    if (isUndefined(data[to])) {
      data[to] = undefined;
    }
    data.splice(to, 0, data.splice(from, 1)[0]);
    return data;
  }

  return [];
};
export default <T>(data: T[], indexA: number, indexB: number): void => {
  data[indexA] = [data[indexB], (data[indexB] = data[indexA])][0];
};
export default function prepend<T>(data: T[], value: T | T[]): T[] {
  return [...(Array.isArray(value) ? value : [value]), ...data];
}
export default function append<T>(data: T[], value: T | T[]): T[] {
  return [...data, ...(Array.isArray(value) ? value : [value])];
}
export default function insert<T>(data: T[], index: number): (T | undefined)[];
export default function insert<T>(
  data: T[],
  index: number,
  value: T | T[],
): T[];
export default function insert<T>(
  data: T[],
  index: number,
  value?: T | T[],
): (T | undefined)[] {
  return [
    ...data.slice(0, index),
    ...(Array.isArray(value) ? value : [value]),
    ...data.slice(index),
  ];
}
export default <T>(value: T | T[]): undefined[] | undefined =>
  Array.isArray(value) ? Array(value.length).fill(undefined) : undefined;
